mapper.select:
	
	
	select *
from (select /*+INDEX_DESC(vacationApplicationTable vactionAppNum)*/ 
            rownum rn,vat.vacationAppNum,vat.id,hs.name,vat.type,vat.term,vat.state,vat.applicationDate,vat.approvalDate,vat.reason
		from vacationApplicationTable vat join vacationScedule hs on  vat.id=hs.id  
        where 
       		
	
	
	
		vat.state not in('신청') and rownum <=(#{pageNum})*#{amount} and vat.term >= #{nowMonth} and vat.term < #{nextMonth}
		)
		where rn>(#{pageNum}-1)*#{amount}

mapper.update:
update vacationApplicationTable 
set approvalDate=sysdate, state='승인'  
where vacationAppNum=#{vacationAppNum}

update vacationApplicationTable 
set approvalDate=sysdate, state='거절',refusalreason=#{refusalreason}   
where vacationAppNum=#{vacationAppNum}

mapper.select:
		select vacationAppNum,id,type,term,state,applicationDate,approvaldate
		from vacationApplicationTable 
 		where id=#{id} and term >= #{cri.nowMonth} and term < #{cri.nextMonth}

select vat.vacationAppNum,vat.id,hs.name,vat.type,vat.term,vat.state,vat.applicationDate,vat.approvalDate,vat.reason,vat.refusalReason
from vacationApplicationTable vat join vacationScedule hs on  vat.id=hs.id
where vat.vacationAppNum=#{vacationAppNum}

mapper.update:
update vacationApplicationTable 
set applicationDate=sysdate,reason=#{reason}
where vacationAppNum=#{vacationAppNum}

update vacationApplicationTable 
set approvalDate=sysdate, state='반납'  
where vacationAppNum=#{vacationAppNum}

mapper.select:
select term 
from vacationApplicationTable 
where vacationAppNum=#{vacationAppNum}
